mapper.select:
SELECT delino, orderno, delivery, ddate 
FROM deli
WHERE delino=#{delino}

SELECT delino, orderno, delivery, ddate 
FROM deli
WHERE orderno=#{orderno}

SELECT count(delino) as cnt 
FROM deli
WHERE orderno=#{orderno}

mapper.update:
UPDATE deli
set delivery=#{delivery}, ddate = sysdate
WHERE delino=#{delino}
