mapper.select:
SELECT categrpno, name, seqno, rdate, cnt
FROM notice_categrp
WHERE categrpno = #{categrpno}

mapper.update:
UPDATE notice_categrp
SET name=#{name}, seqno=#{seqno}
WHERE categrpno = #{categrpno}

UPDATE notice_categrp
SET seqno = seqno + 1
WHERE categrpno=#{categrpno}

UPDATE notice_categrp 
SET cnt = cnt + 1 
WHERE categrpno=#{categrpno}

UPDATE notice_categrp 
SET cnt = cnt - 1 
WHERE categrpno=#{categrpno}
