mapper.update:
UPDATE product
SET name=#{name}, price=#{price} content=#{content}, word=#{word}
WHERE productno = #{productno}

mapper.select:
SELECT COUNT(*) as cnt
FROM product
WHERE productcateno=#{productcateno}

mapper.update:
UPDATE product
SET replycnt = replycnt + 1
WHERE productno = #{productno}

UPDATE product
SET replycnt = replycnt + 1
WHERE productno = #{productno}

mapper.select:
SELECT COUNT(*) as cnt FROM product
WHERE productcateno=#{productcateno}
WHERE productcateno=#{productcateno} AND word LIKE '%' || #{word} || '%'

SELECT productno, productcateno, name, price, content, recom, cnt, replycnt, rdate, word, r FROM( SELECT productno, membersno, productcateno, name, price, content, recom, cnt, replycnt, rdate, word, rownum as r FROM ( SELECT productno, membersno, productcateno, name, price, content, recom, cnt, replycnt, rdate, word FROM product
WHERE productcateno=#{productcateno}
WHERE productcateno=#{productcateno} AND word LIKE '%' || #{word} || '%'
ORDER BY productno DESC ) ) where r > = #{startNum} and r < = #{endNum}
